export const styles = {
  '@keyframes spin': {
    '0%': {
      transform: 'rotate(0deg)'
    },
    '100%': {
      transform: 'rotate(360deg)'
    }
  },
  spinnerSmall: {},
  spinnerMedium: {},
  spinnerLarge: {},
  spinner: {
    '&$spinnerSmall, $spinnerSmall:after': {
      borderRadius: '50%',
      width: '24px',
      height: '24px'
    },
    '&$spinnerMedium, $spinnerMedium:after': {
      borderRadius: '50%',
      width: '40px',
      height: '40px'
    },
    '&$spinnerLarge, $spinnerLarge:after': {
      borderRadius: '50%',
      width: '64px',
      height: '64px'
    },
    margin: 'auto',
    fontSize: '10px',
    position: 'relative',
    textIndent: '-9999em',
    borderTop: '4px solid rgba(54, 64, 81, 0.16)',
    borderRight: '4px solid rgba(54, 64, 81, 0.16)',
    borderBottom: '4px solid rgba(54, 64, 81, 0.16)',
    borderLeft: '4px solid rgba(54, 64, 81, 1)',
    transform: 'translateZ(0)',
    animation: '$spin .8s infinite linear'
  }
};
import { createComponentStyles } from '@ui5/webcomponents-react-base/lib/createComponentStyles';
import { useI18nBundle } from '@ui5/webcomponents-react-base/lib/hooks';
import { StyleClassHelper } from '@ui5/webcomponents-react-base/lib/StyleClassHelper';
import { usePassThroughHtmlProps } from '@ui5/webcomponents-react-base/lib/usePassThroughHtmlProps';
import { PLEASE_WAIT } from '@ui5/webcomponents-react/dist/assets/i18n/i18n-defaults';
import { Size } from '@ui5/webcomponents-react/lib/Size';
import React, { FC, forwardRef, RefObject, useEffect, useState } from 'react';
import { CommonProps } from '../../interfaces/CommonProps';
import { styles } from './Spinner.jss';

export interface SpinnerProps extends CommonProps {
  /*
   * Delay in ms until the Spinner will be displayed
   */
  delay?: number;
  size?: Size;
}

const useStyles = createComponentStyles(styles, { name: 'Spinner' });

/**
 * <code>import { Spinner } from '@ui5/webcomponents-react/lib/Spinner';</code>
 */
const Spinner: FC<SpinnerProps> = forwardRef((props: SpinnerProps, ref: RefObject<HTMLDivElement>) => {
  const { className, size, tooltip, slot, style, delay } = props;
  const classes = useStyles();
  const [isVisible, setIsVisible] = useState(delay === 0);

  const spinnerClasses = StyleClassHelper.of(classes.spinner);
  if (className) {
    spinnerClasses.put(className);
  }

  spinnerClasses.put(classes[`spinner${size}`]);

  useEffect(() => {
    let timeout;
    if (delay > 0) {
      timeout = setTimeout(() => {
        setIsVisible(true);
      }, delay);
    }
    return () => {
      clearTimeout(timeout);
    };
  }, []);

  const passThroughProps = usePassThroughHtmlProps(props);

  const i18nBundle = useI18nBundle('@ui5/webcomponents-react');

  if (!isVisible) {
    return null;
  }

  return (
    <div
      ref={ref}
      className={spinnerClasses.valueOf()}
      data-component-name="Spinner"
      aria-busy="true"
      role="progressbar"
      tabIndex={0}
      aria-valuemin={0}
      aria-valuemax={100}
      title={tooltip || i18nBundle.getText(PLEASE_WAIT)}
      slot={slot}
      style={style}
      {...passThroughProps}
    >
      Loading...
    </div>
  );
});

Spinner.defaultProps = {
  delay: 0,
  size: Size.Medium
};

Spinner.displayName = 'Spinner';

export { Spinner };
