export enum BarDesign {
  Auto = 'Auto',
  Footer = 'Footer',
  Header = 'Header',
  SubHeader = 'SubHeader',
  FloatingFooter = 'FloatingFooter'
}
import { CssSizeVariables } from '@ui5/webcomponents-react-base/lib/CssSizeVariables';
import { ThemingParameters } from '@ui5/webcomponents-react-base/lib/ThemingParameters';

const styles = {
  // outer container, controlling height and width
  bar: {
    width: '100%',
    display: 'block',
    position: 'relative',
    height: CssSizeVariables.sapWcrBarHeight
  },
  // left container
  left: {
    position: 'absolute',
    left: '0',
    top: '0',
    height: '100%',
    paddingLeft: '0.5rem',
    textAlign: 'left',
    display: 'flex',
    alignItems: 'center'
  },
  // center container
  center: {
    textAlign: 'center',
    width: '100%',
    height: '0',
    top: '0',
    left: '0'
  },
  // inner container around left, center, and right
  inner: {
    display: 'inline-flex',
    padding: '0 0.5rem 0 0.5rem',
    alignItems: 'center'
  },
  // right container
  right: {
    position: 'absolute',
    right: '0',
    top: '0',
    height: '100%',
    paddingRight: '0.5rem',
    textAlign: 'right',
    display: 'flex',
    alignItems: 'center'
  },
  auto: {
    height: CssSizeVariables.sapWcrBarHeight,
    background: ThemingParameters.sapPageHeader_Background,
    boxShadow: ThemingParameters.sapContent_HeaderShadow,
    '& $inner': {
      height: CssSizeVariables.sapWcrBarHeight
    }
  },
  subHeader: {
    height: CssSizeVariables.sapWcrSubHeaderBarHeight,
    background: ThemingParameters.sapPageHeader_Background,
    boxShadow: ThemingParameters.sapContent_HeaderShadow,
    paddingBottom: ' 0.25rem',
    '& $inner': {
      height: CssSizeVariables.sapWcrSubHeaderBarHeight
    }
  },
  footer: {
    height: CssSizeVariables.sapWcrBarHeight,
    background: ThemingParameters.sapPageFooter_Background,
    borderTop: `0.0625rem solid ${ThemingParameters.sapPageFooter_BorderColor}`,
    '& $inner': {
      height: CssSizeVariables.sapWcrBarHeight
    }
  },
  floatingFooter: {
    height: CssSizeVariables.sapWcrBarHeight,
    background: ThemingParameters.sapPageFooter_Background,
    // borderRadius: ThemingParameters.sapElement_BorderCornerRadius,
    boxShadow: ThemingParameters.sapContent_Shadow1,
    border: 'none',
    opacity: 1,
    '& $inner': {
      height: CssSizeVariables.sapWcrBarHeight
    }
  }
};

export default styles;
import { StyleClassHelper } from '@ui5/webcomponents-react-base/lib/StyleClassHelper';
import { usePassThroughHtmlProps } from '@ui5/webcomponents-react-base/lib/usePassThroughHtmlProps';
import { useDeprecateRenderMethods } from '@ui5/webcomponents-react-base/lib/hooks';
import React, { FC, forwardRef, ReactNode, Ref } from 'react';
import { createComponentStyles } from '@ui5/webcomponents-react-base/lib/createComponentStyles';
import { CommonProps } from '../../interfaces/CommonProps';
import { BarDesign } from '../../lib/BarDesign';
import styles from './Bar.jss';

export interface BarPropTypes extends CommonProps {
  renderContentLeft?: () => JSX.Element;
  renderContentMiddle?: () => JSX.Element;
  renderContentRight?: () => JSX.Element;
  contentLeft?: ReactNode | ReactNode[];
  contentMiddle?: ReactNode | ReactNode[];
  contentRight?: ReactNode | ReactNode[];
  design?: BarDesign;
}

const useStyles = createComponentStyles(styles, { name: 'Bar' });

/**
 * <code>import { Bar } from '@ui5/webcomponents-react/lib/Bar';</code>
 */
const Bar: FC<BarPropTypes> = forwardRef((props: BarPropTypes, ref: Ref<HTMLDivElement>) => {
  const { className, style, tooltip, slot, design } = props;
  const contentLeft = useDeprecateRenderMethods(props, 'renderContentLeft', 'contentLeft');
  const contentMiddle = useDeprecateRenderMethods(props, 'renderContentMiddle', 'contentMiddle');
  const contentRight = useDeprecateRenderMethods(props, 'renderContentRight', 'contentRight');

  const classes = useStyles();

  const cssClasses = StyleClassHelper.of(classes.bar);
  switch (design) {
    case BarDesign.Footer:
      cssClasses.put(classes.footer);
      break;
    case BarDesign.SubHeader:
      cssClasses.put(classes.subHeader);
      break;
    case BarDesign.FloatingFooter:
      cssClasses.put(classes.floatingFooter);
      break;
    case BarDesign.Header:
    case BarDesign.Auto:
    default:
      cssClasses.put(classes.auto);
  }
  if (className) {
    cssClasses.put(className);
  }

  const passThroughProps = usePassThroughHtmlProps(props);

  return (
    <div
      data-bar-part="Root"
      className={cssClasses.toString()}
      style={style}
      title={tooltip}
      slot={slot}
      ref={ref}
      {...passThroughProps}
    >
      <div data-bar-part="Left" className={classes.left}>
        {contentLeft}
      </div>
      <div data-bar-part="Center" className={classes.center}>
        <div className={classes.inner}>{contentMiddle}</div>
      </div>
      <div data-bar-part="Right" className={classes.right}>
        {contentRight}
      </div>
    </div>
  );
});

Bar.displayName = 'Bar';
Bar.defaultProps = {
  design: BarDesign.Auto
};

export { Bar };
