		if (!this.firstRendering && this.hasValueStateMessage) {
			this.toggle(this.shouldDisplayOnlyValueStateMessage);
		}

		this.firstRendering = false;
	}

	_onkeydown(event) {
		if (isUp(event)) {
			return this._handleUp(event);
		}

		if (isDown(event)) {
			return this._handleDown(event);
		}

		if (isSpace(event)) {
			return this._handleSpace(event);
		}

		if (isEnter(event)) {
			return this._handleEnter(event);
		}

		this._keyDown = true;
	}

	_onkeyup(event) {
		this._keyDown = false;
	}

	/* Event handling */
	_handleUp(event) {
		if (this.Suggestions && this.Suggestions.isOpened()) {
			this.Suggestions.onUp(event);
		}
	}

	_handleDown(event) {
		if (this.Suggestions && this.Suggestions.isOpened()) {
			this.Suggestions.onDown(event);
		}
	}

	_handleSpace(event) {
		if (this.Suggestions) {
			this.Suggestions.onSpace(event);
		}
	}

	_handleEnter(event) {
		const itemPressed = !!(this.Suggestions && this.Suggestions.onEnter(event));
		if (!itemPressed) {
			this.fireEventByAction(this.ACTION_ENTER);
		}
	}

	_onfocusin(event) {
		this.focused = true; // invalidating property
		this.previousValue = this.value;
	}

	_onfocusout(event) {
		// if focusout is triggered by pressing on suggestion item skip invalidation, because re-rendering
		// will happen before "itemPress" event, which will make item "active" state not visualized
