    //Check property exists on both objects
    if (obj1.hasOwnProperty(p) !== obj2.hasOwnProperty(p)) return false;

    switch (typeof obj1[p]) {
      //Deep compare objects
      case 'object':
        if (!deepCompare(obj1[p], obj2[p])) return false;
        break;
      //Compare function code
      case 'function':
        if (typeof obj2[p] == 'undefined' || (p != 'compare' && obj1[p].toString() != obj2[p].toString())) return false;
        break;
      //Compare values
      default:
        if (obj1[p] != obj2[p]) return false;
    }
  }

  //Check object 2 for any extra properties
  for (let p in obj2) {
    if (typeof obj2[p] !== 'undefined' && typeof obj1[p] === 'undefined') {
      return false;
    }
    // if (typeof (obj1[p]) == 'undefined') return false;
  }
  return true;
}
import { deepCompare } from '../util/deepCompare';

export const stateReducer = (newState, action) => {
  const { payload } = action;
  switch (action.type) {
    case 'SET_GROUP_BY':
      if (deepCompare(newState.groupBy, payload)) {
        return newState;
      }
      return { ...newState, groupBy: payload };
    case 'SET_SELECTED_ROWS':
      if (deepCompare(newState.selectedRowIds, action.selectedIds)) {
        return newState;
      }
      return { ...newState, selectedRowIds: action.selectedIds };
    case 'TABLE_RESIZE':
      return { ...newState, tableClientWidth: payload.tableClientWidth };
    case 'TABLE_SCROLLING_ENABLED':
      return { ...newState, isScrollable: payload.isScrollable };
    default:
      return newState;
  }
};
