import { registerThemeProperties } from "@ui5/webcomponents-base/dist/asset-registries/Themes.js";

import defaultThemeBase from "@ui5/webcomponents-theme-base/dist/generated/themes/sap_fiori_3/parameters-bundle.css.js";
import defaultTheme from "./sap_fiori_3/parameters-bundle.css.js";

registerThemeProperties("@ui5/webcomponents-theme-base", "sap_fiori_3", defaultThemeBase);
registerThemeProperties("@ui5/webcomponents", "sap_fiori_3", defaultTheme);
export default ":host(:not([hidden])){display:inline-block}.ui5-segmentedbutton-root{display:flex}::slotted(ui5-togglebutton){border-radius:0;height:var(--_ui5_button_base_height);min-width:2.5rem;white-space:nowrap;overflow:hidden;text-overflow:ellipsis}::slotted(ui5-togglebutton:nth-child(odd)){border:1px solid var(--sapButton_Selected_BorderColor);border-right:0;border-left:0}::slotted(ui5-togglebutton:last-child){border-top-right-radius:.375rem;border-bottom-right-radius:.375rem;border-right:1px solid var(--sapButton_Selected_BorderColor)}::slotted(ui5-togglebutton:first-child){border-top-left-radius:.375rem;border-bottom-left-radius:.375rem;border-left:1px solid var(--sapButton_Selected_BorderColor)}";
